import React, { useEffect, useState, useRef } from "react";
import "./Home.css";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { Link } from "react-router-dom";

export default function Home() {
  const picLink = "https://cdn-icons-png.flaticon.com/128/3177/3177440.png";
  const navigate = useNavigate();

  const [data, setData] = useState([]);
  const [comment, setComment] = useState("");
  const [show, setShow] = useState(false);
  const [item, setItem] = useState({});

  // UseRef to persist values
  const limit = 10;
  const skipRef = useRef(0);

  const notifyA = (msg) => toast.error(msg);
  const notifyB = (msg) => toast.success(msg);

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      navigate("./signup");
    }

    fetchPosts();

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Fetch posts with limit/skip
  const fetchPosts = () => {
    fetch(`/allposts?limit=${limit}&skip=${skipRef.current}`, {
      headers: {
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
    })
      .then((res) => res.json())
      .then((result) => {
        setData((prev) => [...prev, ...result]);
      })
      .catch((err) => console.log(err));
  };

  const handleScroll = () => {
    if (
      document.documentElement.clientHeight + window.pageYOffset >=
      document.documentElement.scrollHeight - 2
    ) {
      skipRef.current += 10;
      fetchPosts();
    }
  };

  const toggleComment = (posts) => {
    setShow(!show);
    if (!show) setItem(posts);
  };

  const likePost = (id) => {
    fetch("/like", {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
      body: JSON.stringify({ postId: id }),
    })
      .then((res) => res.json())
      .then((result) => {
        const newData = data.map((p) =>
          p._id === result._id ? result : p
        );
        setData(newData);
      });
  };

  const unlikePost = (id) => {
    fetch("/unlike", {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
      body: JSON.stringify({ postId: id }),
    })
      .then((res) => res.json())
      .then((result) => {
        const newData = data.map((p) =>
          p._id === result._id ? result : p
        );
        setData(newData);
      });
  };

  const makeComment = (text, id) => {
    if (!text.trim()) return;

    fetch("/comment", {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
      body: JSON.stringify({ text, postId: id }),
    })
      .then((res) => res.json())
      .then((result) => {
        const updatedPosts = data.map((p) =>
          p._id === result._id ? result : p
        );

        setData(updatedPosts);
        setComment("");
        notifyB("Comment posted");

        if (show && item._id === id) {
          setItem(result);
        }
      });
  };

  return (
    <div className="home">
      {data.map((posts) => {
        return (
          <div className="card" key={posts._id}>
            <div className="card-header">
              <div className="card-pic">
                <img
                  src={posts.postedBy.Photo || picLink}
                  alt="profile"
                />
              </div>
              <h5>
                <Link to={`/profile/${posts.postedBy._id}`}>
                  {posts.postedBy.name}
                </Link>
              </h5>
            </div>

            <div className="card-image">
              <img src={posts.photo} alt="post" />
            </div>

            <div className="card-content">
              {posts.likes.includes(
                JSON.parse(localStorage.getItem("user"))._id
              ) ? (
                <span
                  className="material-symbols-outlined material-symbols-outlined-red"
                  onClick={() => unlikePost(posts._id)}
                >
                  favorite
                </span>
              ) : (
                <span
                  className="material-symbols-outlined"
                  onClick={() => likePost(posts._id)}
                >
                  favorite
                </span>
              )}

              <p>{posts.likes.length} Likes</p>
              <p>{posts.body}</p>

              <p
                style={{ fontWeight: "bold", cursor: "pointer" }}
                onClick={() => toggleComment(posts)}
              >
                View all comments
              </p>
            </div>

            <div className="add-comment">
              <span className="material-symbols-outlined">mood</span>
              <input
                type="text"
                placeholder="Add a comment"
                value={comment}
                onChange={(e) => setComment(e.target.value)}
              />
              <button
                className="comment"
                onClick={() => makeComment(comment, posts._id)}
              >
                Post
              </button>
            </div>
          </div>
        );
      })}

      {show && (
        <div className="showComment">
          <div className="container">
            <div className="postPic">
              <img src={item.photo} alt="big-pic" />
            </div>

            <div className="details">
              <div
                className="card-header"
                style={{ borderBottom: "1px solid #00000029" }}
              >
                <div className="card-pic">
                  <img
                    src={item.postedBy.Photo || picLink}
                    alt=""
                  />
                </div>
                <h5>{item.postedBy.name}</h5>
              </div>

              <div
                className="comment-section"
                style={{ borderBottom: "1px solid #00000029" }}
              >
                {item.comments.map((c) => (
                  <p className="comm" key={c._id}>
                    <span className="commenter" style={{ fontWeight: "bolder" }}>
                      {c.postedBy.name}{" "}
                    </span>
                    <span className="commentText">{c.comment}</span>
                  </p>
                ))}
              </div>

              <div className="card-content">
                <p>{item.likes.length} Likes</p>
                <p>{item.body}</p>
              </div>

              <div className="add-comment">
                <span className="material-symbols-outlined">mood</span>
                <input
                  type="text"
                  placeholder="Add a comment"
                  value={comment}
                  onChange={(e) => setComment(e.target.value)}
                />
                <button
                  className="comment"
                  onClick={() => {
                    makeComment(comment, item._id);
                  }}
                >
                  Post
                </button>
              </div>
            </div>
          </div>

          <div
            className="close-comment"
            onClick={() => setShow(false)}
          >
            <span className="material-symbols-outlined material-symbols-outlined-comment">
              close
            </span>
          </div>
        </div>
      )}
    </div>
  );
}
