//Display all posts from the backend.Allow the logged-in user to:Like / Unlike posts, Add comments. View comments in a popup

//Show user info and profile pics. Link to user profiles when clicking the name
import React, { useEffect, useState } from "react";
import "./Home.css";
import { useNavigate, Link } from "react-router-dom";
import { toast } from "react-toastify";

export default function Home() {
  const navigate = useNavigate();
  //posts → array of all posts fetched from backend
  const [posts, setPosts] = useState([]);
  //showCommentsFor → stores post ID of the currently opened comment popup
  const [showCommentsFor, setShowCommentsFor] = useState(null); // post id for comment popup
  const [commentsInput, setCommentsInput] = useState({}); // store comment per post

  const picLink = "https://cdn-icons-png.flaticon.com/128/3177/3177440.png";

  const notifyA = (msg) => toast.error(msg);
  const notifyB = (msg) => toast.success(msg);

//Reads the currently logged-in user from localStorage. Needed for things like:

//Checking if the user already liked a post

//Displaying user’s profile pic when commenting
  const user = JSON.parse(localStorage.getItem("user"));

  // Fetch posts on mount
  //On component mount:

//Checks if user is logged in (JWT in localStorage), Redirects to signup if not logged in, Fetches all posts from backend, Saves them in posts state
  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      navigate("/signup");
      return;
    }

    fetch("http://localhost:5000/allposts", {
      headers: { Authorization: "Bearer " + token },
    })
      .then((res) => res.json())
      .then((result) => setPosts(result))
      .catch((err) => console.log(err));
  }, []);

  // Like / Unlike
  // Sends a PUT request to /like or /unlike backend endpoint, Backend returns the updated post object

//Updates posts array with the new likes count, Uses post._id === result._id to replace the updated post
  const updateLike = (id, action) => {
    fetch(`http://localhost:5000/${action}`, {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
      body: JSON.stringify({ postId: id }),
    })
      .then((res) => res.json())
      .then((result) => {
        const updatedPosts = posts.map((p) => (p._id === result._id ? result : p));
        setPosts(updatedPosts);
      });
  };

  // Add comment
  //Sends a PUT request to /comment backend endpoint, Updates the posts state with the new comment

//Clears the input field for that specific post, Shows a toast notification: "Comment posted"
//Important: Because commentsInput is an object keyed by post ID, each post has its own comment input — no conflicts between multiple posts.
  const makeComment = (text, id) => {
    if (!text) return;
    fetch("http://localhost:5000/comment", {
      method: "put",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + localStorage.getItem("jwt"),
      },
      body: JSON.stringify({ text, postId: id }),
    })
      .then((res) => res.json())
      .then((result) => {
        const updatedPosts = posts.map((p) => (p._id === result._id ? result : p));
        setPosts(updatedPosts);
        setCommentsInput((prev) => ({ ...prev, [id]: "" }));
        notifyB("Comment posted");
      });
  };

  // Toggle comment popup
  //Clicking "View all comments" opens a popup overlay for that post, Clicking close or the same button again closes it
  const toggleCommentPopup = (id = null) => {
    setShowCommentsFor((prev) => (prev === id ? null : id));
  };

  // Update localStorage user for profile pic change
  const refreshUser = () => {
    const updatedUser = JSON.parse(localStorage.getItem("user"));
    setPosts((prevPosts) => [...prevPosts]); // force re-render
  };
// Loops through all posts and renders a card for each. Each post card includes:

//Header: User photo + username (links to profile)
//Image: Post picture

//Content:Like/unlike heart button, Likes count

//Caption:"View all comments" button

//Add comment input: connected to commentsInput[post._id]

//Comment popup (only if showCommentsFor === post._id)
  return (
    <div className="home">

      {posts.map((post) => {
        const liked = post.likes.includes(user._id);
        return (
          <div className="card" key={post._id}>
            <div className="card-header">
              <div className="card-pic">
                <img src={post.postedBy.Photo || picLink} alt="" />
              </div>
              <h5>
                <Link to={`/profile/${post.postedBy._id}`}>{post.postedBy.name}</Link>
              </h5>
            </div>

            <div className="card-image">
              <img src={post.photo} alt="" />
            </div>

            <div className="card-content">
              <span
                className={`material-symbols-outlined ${liked ? "material-symbols-outlined-red" : ""}`}
                onClick={() => updateLike(post._id, liked ? "unlike" : "like")}
              >
                favorite
              </span>
              <p>{post.likes.length} Likes</p>
              <p>{post.body}</p>
              <p
                style={{ fontWeight: "bold", cursor: "pointer" }}
                onClick={() => toggleCommentPopup(post._id)}
              >
                View all comments
              </p>
            </div>

            {/* Add Comment */}
            <div className="add-comment">
              <span className="material-symbols-outlined">mood</span>
              <input
                type="text"
                placeholder="Add a comment"
                value={commentsInput[post._id] || ""}
                onChange={(e) =>
                  setCommentsInput((prev) => ({ ...prev, [post._id]: e.target.value }))
                }
              />
              <button
                className="comment"
                onClick={() => makeComment(commentsInput[post._id], post._id)}
              >
                Post
              </button>
            </div>

            {/* Show Comment Popup */}
            {/*Shows full list of comments for that post

            Shows post image, author, caption, likes

               Includes input to add new comments
             Close button to hide popup*/}
            {showCommentsFor === post._id && (
              <div className="showComment">
                <div className="container">
                  <div className="postPic">
                    <img src={post.photo} alt="" />
                  </div>
                  <div className="details">
                    <div className="card-header" style={{ borderBottom: "1px solid #00000029" }}>
                      <div className="card-pic">
                        <img src={post.postedBy.Photo || picLink} alt="" />
                      </div>
                      <h5>{post.postedBy.name}</h5>
                    </div>

                    <div className="comment-section" style={{ borderBottom: "1px solid #00000029" }}>
                      {post.comments.map((c, index) => (
                        <p className="comm" key={index}>
                          <span className="commenter" style={{ fontWeight: "bolder" }}>
                            {c.postedBy.name}{" "}
                          </span>
                          <span className="commentText">{c.comment}</span>
                        </p>
                      ))}
                    </div>

                    <div className="card-content">
                      <p>{post.likes.length} Likes</p>
                      <p>{post.body}</p>
                    </div>

                    <div className="add-comment">
                      <span className="material-symbols-outlined">mood</span>
                      <input
                        type="text"
                        placeholder="Add a comment"
                        value={commentsInput[post._id] || ""}
                        onChange={(e) =>
                          setCommentsInput((prev) => ({ ...prev, [post._id]: e.target.value }))
                        }
                      />
                      <button
                        className="comment"
                        onClick={() => {
                          makeComment(commentsInput[post._id], post._id);
                        }}
                      >
                        Post
                      </button>
                    </div>
                  </div>
                </div>
                <div className="close-comment" onClick={() => toggleCommentPopup(post._id)}>
                  <span className="material-symbols-outlined material-symbols-outlined-comment">
                    close
                  </span>
                </div>
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
}
