import React, { useState } from "react";
import "./App.css";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import Home from "./components/Home";
import SignUp from "./components/SignUp";
import SignIn from "./components/SignIn";
import Profile from "./components/Profie";
import Createpost from "./components/Createpost";
import UserProfile from "./components/UserProfile";
import MyFollowingPost from "./components/MyFollowingPost";
import Modal from "./components/Modal";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { UserProvider } from "./components/UserContext.js"; // ✅ Use UserContext

function App() {
  const [modalOpen, setModalOpen] = useState(false);

  return (
    <UserProvider>
      <BrowserRouter>
        <div className="App">
          <Navbar />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/signup" element={<SignUp />} />
            <Route path="/signin" element={<SignIn />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="/createPost" element={<Createpost />} />
            <Route path="/profile/:userid" element={<UserProfile />} />
            <Route path="/followingpost" element={<MyFollowingPost />} />
          </Routes>
          <ToastContainer theme="dark" />

          {modalOpen && <Modal setModalOpen={setModalOpen} />}
        </div>
      </BrowserRouter>
    </UserProvider>
  );
}

export default App;
